import React, { useEffect, useState } from "react";
import "./App.css";
import Box from "@mui/material/Box";
import ToggleButton from "@mui/material/ToggleButton";
import ToggleButtonGroup from "@mui/material/ToggleButtonGroup";
import Button from "@mui/material/Button";
import Alert from "@material-ui/lab/Alert";
import AlertTitle from "@material-ui/lab/AlertTitle";
import Grid from "@mui/material/Grid";
import axios from "axios";

import CircularProgress from '@mui/material/CircularProgress';




//main color rgb(35 36 44);
//box color rgb(57 58 66)

function App() {
  const [alignment, setAlignment] = React.useState("web");
  const [alignment2, setAlignment2] = React.useState("web2");
  const [file, getFile] = useState(null);
  const [tree, setTree] = React.useState(0);

  const [progressBar, setProgressBar] = React.useState(false);
  const [succesBar, setSuccesBar] = React.useState(false);
  // For the error box
  const [fileFormatError, setErrorFileFormat] = React.useState(false);

  const download = async () => {
    await onDrop();
    console.log("in download front end");
    //const fileName = e.target.parentElement.id;
    axios({
      method: "get",
      url: "http://localhost:3001/download",
      responseType: "blob",
      headers: {},
    })
      .then((res) => {
        const url = window.URL.createObjectURL(new Blob([res.data]));
        const link = document.createElement("a");
        link.href = url;
        link.setAttribute("download", "Final.mp4");
        document.body.appendChild(link);
        link.click();
      })

      .catch((error) => {
        alert(error);
      });
  };

  const onDrop = async () => {

    setProgressBar(true);
    var formData = new FormData();
    formData.append("file", file);
    try {
      const res = await axios.post("http://localhost:3001/upload", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      })
      return (res);
      //console.log(res);
    } catch (err) {
      if (err) {
        console.log("this is the err", err);

        setErrorFileFormat(true);
      } else {
        setProgressBar(false);
        setSuccesBar(true);
        console.log(err.response.data.msg);
       console.log("finished in front end")
      }
    }
  };
  const handleChange = (event, newAlignment) => {
    setAlignment(newAlignment);
  };
  const handleChangeSub = (event, newAlignment2) => {
    setAlignment2(newAlignment2);
  };
  return (
    <div className="App" id="App">
      <div className="box-2">
        <Box
          sx={{
            width: "75vw",
            height: "85wh",
            backgroundColor: "rgb(57 58 66)",
            alignItems: "center",
            fontSize: "30",
            borderRadius: 2,
            marginTop: "10vw",
            textAlign: "center",
          }}
        >
          <div className="box-title">
            <h1 className="title">SÃ¼B </h1>
            </div>
            <div className="presentation-box">

            <p className="title">
              Send us your video and we will add subtitle to it
              </p>
          </div>
          <div className="downloadBox">

            {/*This is the box to palce the files */}
            <Box component="span" sx={{ p: 1, border: "1px dashed grey", width:'40%' }}>
              <Button
                style={{ fontWeight: "bold", fontSize: "18px", color: "white", width:'100%'}}
              >
                <input
                className="noFileChosenButton"
                  type="file"
                  onChange={(e) => getFile(e.target.files[0])}
                />

              </Button>
            </Box>
          </div>
          <Button color="primary" onClick={download}
          style={{
            borderRadius: 35,
            backgroundColor: "rgb(0,0,0)",
            padding: "10px 26px",
            fontSize: "15px",
            marginTop: '30px',
            marginLeft:'0',
            marginBottom:'30px',
            color:'rgb(255,255,255)'

        }}>
            Send video
          </Button>
        </Box>
      </div>

      {/* This is the alertBox  */}
      {fileFormatError ? (
        <div className="alertDiv">
          <Box display="flex" alignItems="center" justifyContent="center">
            <Alert severity="error">Sorry, we only accept mp4 files</Alert>
          </Box>
        </div>
      ) : (
        <div> </div>
      )}


      {progressBar ? (
        <div>
         <CircularProgress disableShrink />

          </div>

      ) : (<div> </div>)}
    </div>
  );
}

export default App;
